package basic_004_datatypes_variables;

/**
 * @author lenovo write a java program to print all the data types with default
 *         parameters
 */
public class A002_instance_variable {
	int i;
	String s;
	short s1;
	float f;
	long l;
	double d;
	char c;
	boolean b;
	byte b1;
	
	

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		A002_instance_variable obj = new A002_instance_variable();
		System.out.println(obj.i);
		System.out.println(obj.s);
		System.out.println(obj.s1);
		System.out.println(obj.f);
		System.out.println(obj.l);
		System.out.println(obj.d);
		System.out.println(obj.c);
		System.out.println(obj.b);
		System.out.println(obj.b1);
		
				

	}

}
