package basic_005_relational_operators;

/**
 * @author lenovo
 *
 */
public class A008_bitwise_operator {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5;
		int b = 6;
		System.out.println(a&b);
		System.out.println(a|b);
		System.out.println(a^b);
		System.out.println(~a);
		System.out.println(a<<b);
		System.out.println(a>>b);

	}

}
